import 'package:flutter/material.dart';

class W10D4Slivers extends StatelessWidget {
  const W10D4Slivers({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: const Text('Flutter Slivers Example'),
        ),
        body: CustomScrollView(
          slivers: <Widget>[
            SliverAppBar(
              //  expandedHeight: 200.0,
              flexibleSpace: FlexibleSpaceBar(
                title: const Text('Sliver Example'),
                background: Image.network(
                  'data:image/jpeg;base64,',
                  fit: BoxFit.cover,
                ),
              ),
            ),
            SliverList(
              delegate: SliverChildBuilderDelegate(
                (BuildContext context, int index) {
                  return ListTile(
                    title: Text('Item No = $index'),
                  );
                },
                childCount: 10, // Number of items in the list
              ),
            ),
          ],
        ),
      ),
    );
  }
}
